import { createContext, useState, useEffect } from "react";
import axios from "axios";

export const ProductContext = createContext();

export const ProductProvider = ({ children }) => {
    const [products, setProducts] = useState([]);
    const [selectedProduct, setSelectedProduct] = useState(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await axios.get("http://localhost:9999/products");
                setProducts(response.data);
            } catch (error) {
                console.error("Error fetching products:", error.message);
            }
        };

        fetchProducts();
    }, []);

    // Tự động đồng bộ selectedProduct với danh sách sản phẩm nếu dữ liệu bị thay đổi
    useEffect(() => {
        if (selectedProduct) {
            const updatedProduct = products.find((product) => product.id == selectedProduct.id);
            if (updatedProduct) setSelectedProduct(updatedProduct);
        }
    }, [products]);

    return (
        <ProductContext.Provider value={{ products, selectedProduct, setSelectedProduct, setProducts }}>
            {children}
        </ProductContext.Provider>
    );
};
